contracts/core/EscrowManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/ISynkioProtocol.sol";

contract EscrowManager is ReentrancyGuard, Ownable, Pausable, ISynkioProtocol {
    using SafeERC20 for IERC20;
    uint256 public constant PLATFORM_FEE_PERCENT = 250; // 2.5%
    uint256 public constant DISPUTE_TIMEOUT = 7 days;
    uint256 public constant ESCROW_TIMEOUT = 30 days;

    uint256 public nextEscrowId = 1;
    uint256 public totalVolume;
    uint256 public totalFees;

    mapping(uint256 => Escrow) public escrows;
    mapping(uint256 => Milestone[]) public milestones;
    mapping(uint256 => Dispute) public disputes;
    mapping(address => uint256) public userEscrowCount;
    mapping(address => bool) public supportedTokens;

    struct TokenInfo {
        address token;
        uint256 chainId;
        string symbol;
        uint8 decimals;
        bool active;
    }

    mapping(address => TokenInfo) public tokenInfo;

    address public paymentProcessor;
    address public reputationRegistry;
    address public disputeResolution;

    modifier onlyEscrowParty(uint256 escrowId) {
        Escrow memory escrow = escrows[escrowId];
        require(
            msg.sender == escrow.buyer || msg.sender == escrow.seller,
            "Not authorized"
        );
        _;
    }

    modifier onlyValidEscrow(uint256 escrowId) {
        require(escrowId > 0 && escrowId < nextEscrowId, "Invalid escrow");
        _;
    }

    modifier onlyBaseSepoliaToken(address token) {
        require(
            token == address(0) || supportedTokens[token],
            "Token not supported on Base Sepolia"
        );
        _;
    }

    constructor() Ownable(msg.sender) {
    }

    function setContracts(
        address _paymentProcessor,
        address _reputationRegistry,
        address _disputeResolution
    ) external onlyOwner {
        paymentProcessor = _paymentProcessor;
        reputationRegistry = _reputationRegistry;
        disputeResolution = _disputeResolution;
    }

    function setSupportedToken(address token, bool supported) external onlyOwner {
        supportedTokens[token] = supported;
        emit TokenSupported(token, supported);
    }

    function addToken(
        address token,
        uint256 chainId,
        string memory symbol,
        uint8 decimals,
        bool active
    ) external onlyOwner {
        require(token != address(0), "Invalid token address");
        tokenInfo[token] = TokenInfo({
            token: token,
            chainId: chainId,
            symbol: symbol,
            decimals: decimals,
            active: active
        });
        supportedTokens[token] = active;
        emit TokenAdded(token, chainId, active);
    }

    function getTokenInfo(address token) external view returns (TokenInfo memory) {
        return tokenInfo[token];
    }

    function updateTokenStatus(address token, bool active) external onlyOwner {
        require(tokenInfo[token].token != address(0), "Token not registered");
        tokenInfo[token].active = active;
        supportedTokens[token] = active;
        emit TokenSupported(token, active);
    }

    function createEscrow(
        address seller,
        string memory description,
        bytes32 metadataHash,
        Milestone[] memory _milestones,
        address token,
        uint256 amount
    ) external payable whenNotPaused onlyBaseSepoliaToken(token) returns (uint256) {
        require(seller != address(0), "Invalid seller");
        require(seller != msg.sender, "Cannot escrow with self");

        uint256 escrowAmount;
        uint256 platformFee;

        bool isERC20 = token != address(0);

        if (token == address(0)) {
            require(msg.value > 0, "Amount must be greater than 0");
            platformFee = (msg.value * PLATFORM_FEE_PERCENT) / 10000;
            escrowAmount = msg.value - platformFee;
        } else {
            require(amount > 0, "Amount must be greater than 0");
            require(supportedTokens[token], "Token not supported");
            platformFee = (amount * PLATFORM_FEE_PERCENT) / 10000;
            escrowAmount = amount - platformFee;
            IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        }

        uint256 escrowId = nextEscrowId++;

        escrows[escrowId] = Escrow({
            id: escrowId,
            buyer: msg.sender,
            seller: seller,
            amount: escrowAmount,
            platformFee: platformFee,
            createdAt: block.timestamp,
            expiresAt: block.timestamp + ESCROW_TIMEOUT,
            status: isERC20 ? EscrowStatus.Funded : EscrowStatus.Pending,
            description: description,
            metadataHash: metadataHash,
            token: token
        });

        // Add milestones if provided
        if (_milestones.length > 0) {
            uint256 totalMilestoneAmount = 0;
            for (uint256 i = 0; i < _milestones.length; i++) {
                milestones[escrowId].push(_milestones[i]);
                totalMilestoneAmount += _milestones[i].amount;
            }
            require(totalMilestoneAmount == escrowAmount, "Milestone amounts must equal escrow amount");
        }

        userEscrowCount[msg.sender]++;
        userEscrowCount[seller]++;
        totalVolume += isERC20 ? amount : msg.value;
        totalFees += platformFee;

        emit EscrowCreated(escrowId, msg.sender, seller, escrowAmount);

        if (isERC20) {
            emit EscrowFunded(escrowId, amount);
        }

        return escrowId;
    }

    function fundEscrow(uint256 escrowId) 
        external 
        payable 
        onlyValidEscrow(escrowId)
        whenNotPaused 
    {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.status == EscrowStatus.Pending, "Escrow not pending");
        require(msg.sender == escrow.buyer, "Only buyer can fund");
        require(msg.value == escrow.amount + escrow.platformFee, "Incorrect amount");

        escrow.status = EscrowStatus.Funded;

        emit EscrowFunded(escrowId, msg.value);
    }

    function releasePayment(uint256 escrowId, uint256 milestoneIndex) 
        external 
        onlyValidEscrow(escrowId)
        onlyEscrowParty(escrowId)
        nonReentrant
    {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.status == EscrowStatus.Funded, "Escrow not funded");
        require(block.timestamp <= escrow.expiresAt, "Escrow expired");

        uint256 releaseAmount;

        if (milestones[escrowId].length > 0) {
            require(milestoneIndex < milestones[escrowId].length, "Invalid milestone");
            Milestone storage milestone = milestones[escrowId][milestoneIndex];
            require(!milestone.completed, "Milestone already completed");

            milestone.completed = true;
            milestone.completedAt = block.timestamp;
            releaseAmount = milestone.amount;
        } else {
            require(msg.sender == escrow.buyer, "Only buyer can release full payment");
            releaseAmount = escrow.amount;
            escrow.status = EscrowStatus.Completed;
        }

        if (escrow.token == address(0)) {
            (bool success, ) = escrow.seller.call{value: releaseAmount}("");
            require(success, "Transfer failed");
        } else {
            IERC20(escrow.token).safeTransfer(escrow.seller, releaseAmount);
        }

        emit EscrowCompleted(escrowId, releaseAmount);

        // Update reputation
        if (reputationRegistry != address(0)) {
            IReputationRegistry(reputationRegistry).updateReputation(
                escrow.seller,
                true,
                releaseAmount
            );
        }
    }

    function fileDispute(uint256 escrowId, string memory reason) 
        external 
        onlyValidEscrow(escrowId)
        onlyEscrowParty(escrowId)
    {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.status == EscrowStatus.Funded, "Escrow not funded");
        require(disputes[escrowId].createdAt == 0, "Dispute already filed");

        disputes[escrowId] = Dispute({
            escrowId: escrowId,
            initiator: msg.sender,
            reason: reason,
            createdAt: block.timestamp,
            status: DisputeStatus.Pending,
            arbitrator: address(0)
        });

        escrow.status = EscrowStatus.Disputed;

        emit EscrowDisputed(escrowId, msg.sender, reason);
    }

    function resolveDispute(uint256 escrowId, bool buyerWins) 
        external 
        onlyValidEscrow(escrowId)
    {
        require(msg.sender == disputeResolution, "Only dispute resolution contract");

        Escrow storage escrow = escrows[escrowId];
        Dispute storage dispute = disputes[escrowId];

        require(escrow.status == EscrowStatus.Disputed, "No active dispute");
        require(dispute.status == DisputeStatus.Pending, "Dispute already resolved");

        dispute.status = DisputeStatus.Resolved;
        dispute.arbitrator = msg.sender;

        address winner = buyerWins ? escrow.buyer : escrow.seller;
        address loser = buyerWins ? escrow.seller : escrow.buyer;

        if (escrow.token == address(0)) {
            (bool success, ) = winner.call{value: escrow.amount}("");
            require(success, "Transfer failed");
        } else {
            IERC20(escrow.token).safeTransfer(winner, escrow.amount);
        }

        escrow.status = EscrowStatus.Completed;

        emit DisputeResolved(escrowId, msg.sender, buyerWins);

        // Update reputation
        if (reputationRegistry != address(0)) {
            IReputationRegistry(reputationRegistry).updateReputation(winner, true, escrow.amount);
            IReputationRegistry(reputationRegistry).updateReputation(loser, false, escrow.amount);
        }
    }

    function cancelEscrow(uint256 escrowId) 
        external 
        onlyValidEscrow(escrowId)
        onlyEscrowParty(escrowId)
    {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.status == EscrowStatus.Pending, "Cannot cancel funded escrow");

        escrow.status = EscrowStatus.Cancelled;

        if (escrow.token == address(0)) {
            (bool success, ) = escrow.buyer.call{value: escrow.amount + escrow.platformFee}("");
            require(success, "Refund failed");
        } else {
            IERC20(escrow.token).safeTransfer(escrow.buyer, escrow.amount + escrow.platformFee);
        }
    }

    function expireEscrow(uint256 escrowId) 
        external 
        onlyValidEscrow(escrowId)
    {
        Escrow storage escrow = escrows[escrowId];
        require(escrow.status == EscrowStatus.Funded, "Escrow not funded");
        require(block.timestamp > escrow.expiresAt, "Escrow not expired");

        escrow.status = EscrowStatus.Expired;

        if (escrow.token == address(0)) {
            (bool success, ) = escrow.buyer.call{value: escrow.amount}("");
            require(success, "Refund failed");
        } else {
            IERC20(escrow.token).safeTransfer(escrow.buyer, escrow.amount);
        }
    }

    function getEscrow(uint256 escrowId) external view returns (Escrow memory) {
        return escrows[escrowId];
    }

    function getMilestones(uint256 escrowId) external view returns (Milestone[] memory) {
        return milestones[escrowId];
    }

    function getDispute(uint256 escrowId) external view returns (Dispute memory) {
        return disputes[escrowId];
    }

    function withdrawFees() external onlyOwner {
        uint256 amount = address(this).balance;
        require(amount > 0, "No fees to withdraw");

        (bool success, ) = owner().call{value: amount}("");
        require(success, "Withdrawal failed");
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }
}

interface IReputationRegistry {
    function updateReputation(address user, bool success, uint256 amount) external;
}

contracts/core/PaymentProcessor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../interfaces/ISynkioProtocol.sol";

contract PaymentProcessor is ReentrancyGuard, Ownable {
    struct FeeDistribution {
        uint256 protocolFee;
        uint256 referrerFee;
        address referrer;
    }

    mapping(address => uint256) public protocolFees;
    mapping(address => uint256) public referrerFees;
    mapping(address => bool) public supportedTokens;

    address public escrowManager;
    address public treasury;
    uint256 public constant PROTOCOL_FEE_PERCENT = 200; // 2%
    uint256 public constant REFERRER_FEE_PERCENT = 50;  // 0.5%

    event PaymentProcessed(
        address indexed token,
        address indexed from,
        address indexed to,
        uint256 amount,
        uint256 protocolFee,
        uint256 referrerFee
    );

    event TokenSupported(address indexed token, bool supported);

    constructor(address _treasury) Ownable(msg.sender) {
        treasury = _treasury;
        supportedTokens[address(0)] = true; // ETH
    }

    function setEscrowManager(address _escrowManager) external onlyOwner {
        escrowManager = _escrowManager;
    }

    function setSupportedToken(address token, bool supported) external onlyOwner {
        supportedTokens[token] = supported;
        emit TokenSupported(token, supported);
    }

    function processPayment(
        address token,
        address from,
        address to,
        uint256 amount,
        address referrer
    ) external nonReentrant returns (FeeDistribution memory) {
        require(msg.sender == escrowManager, "Only escrow manager");
        require(supportedTokens[token], "Token not supported");
        require(amount > 0, "Amount must be greater than 0");

        FeeDistribution memory fees = calculateFees(amount, referrer);

        if (token == address(0)) {
            // ETH payment
            require(address(this).balance >= amount, "Insufficient ETH balance");

            // Transfer to recipient
            (bool success, ) = to.call{value: amount - fees.protocolFee - fees.referrerFee}("");
            require(success, "ETH transfer failed");

            // Collect protocol fee
            if (fees.protocolFee > 0) {
                protocolFees[treasury] += fees.protocolFee;
            }

            // Collect referrer fee
            if (fees.referrerFee > 0 && referrer != address(0)) {
                referrerFees[referrer] += fees.referrerFee;
            }
        } else {
            // ERC20 payment
            IERC20(token).transferFrom(from, address(this), amount);

            // Transfer to recipient
            IERC20(token).transfer(to, amount - fees.protocolFee - fees.referrerFee);

            // Collect protocol fee
            if (fees.protocolFee > 0) {
                protocolFees[treasury] += fees.protocolFee;
            }

            // Collect referrer fee
            if (fees.referrerFee > 0 && referrer != address(0)) {
                referrerFees[referrer] += fees.referrerFee;
            }
        }

        emit PaymentProcessed(token, from, to, amount, fees.protocolFee, fees.referrerFee);

        return fees;
    }

    function calculateFees(uint256 amount, address referrer) 
        public 
        pure 
        returns (FeeDistribution memory) 
    {
        uint256 protocolFee = (amount * PROTOCOL_FEE_PERCENT) / 10000;
        uint256 referrerFee = 0;

        if (referrer != address(0)) {
            referrerFee = (amount * REFERRER_FEE_PERCENT) / 10000;
        }

        return FeeDistribution({
            protocolFee: protocolFee,
            referrerFee: referrerFee,
            referrer: referrer
        });
    }

    function withdrawProtocolFees(address token) external onlyOwner {
        uint256 amount = protocolFees[treasury];
        require(amount > 0, "No fees to withdraw");

        protocolFees[treasury] = 0;

        if (token == address(0)) {
            (bool success, ) = treasury.call{value: amount}("");
            require(success, "ETH withdrawal failed");
        } else {
            IERC20(token).transfer(treasury, amount);
        }
    }

    function withdrawReferrerFees(address token, address referrer) external {
        uint256 amount = referrerFees[referrer];
        require(amount > 0, "No fees to withdraw");
        require(msg.sender == referrer || msg.sender == owner(), "Not authorized");

        referrerFees[referrer] = 0;

        if (token == address(0)) {
            (bool success, ) = referrer.call{value: amount}("");
            require(success, "ETH withdrawal failed");
        } else {
            IERC20(token).transfer(referrer, amount);
        }
    }

    function getFees(address token, address account) external view returns (uint256) {
        if (token == address(0)) {
            return protocolFees[account] + referrerFees[account];
        } else {
            return protocolFees[account] + referrerFees[account];
        }
    }

    receive() external payable {}
}

contracts/core/ReputationRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../interfaces/ISynkioProtocol.sol";

contract ReputationRegistry is Ownable, ISynkioProtocol {
    struct ReputationData {
        uint256 score;
        uint256 totalTransactions;
        uint256 completedTransactions;
        uint256 disputes;
        uint256 totalVolume;
        uint256 lastUpdated;
        string email;
        bool isVendor;
    }

    mapping(address => ReputationData) public reputations;
    mapping(string => address) public emailToAddress;

    uint256 public nextTokenId = 1;

    event ReputationUpdated(address indexed user, uint256 newReputation);
    event UserRegistered(address indexed user, string email, bool isVendor);

    constructor() Ownable(msg.sender) {}

    function registerUser(address user, string calldata email, bool vendorStatus) external onlyOwner {
        require(reputations[user].lastUpdated == 0, "User already registered");

        reputations[user] = ReputationData({
            score: 500, // Starting reputation
            totalTransactions: 0,
            completedTransactions: 0,
            disputes: 0,
            totalVolume: 0,
            lastUpdated: block.timestamp,
            email: email,
            isVendor: vendorStatus
        });

        emailToAddress[email] = user;
        emit UserRegistered(user, email, vendorStatus);
    }

    function updateReputation(address user, uint256 newReputation) external onlyOwner {
        require(reputations[user].lastUpdated > 0, "User not registered");
        require(newReputation >= 0 && newReputation <= 1000, "Invalid reputation score");

        reputations[user].score = newReputation;
        reputations[user].lastUpdated = block.timestamp;

        emit ReputationUpdated(user, newReputation);
    }

    function updateReputation(address user, bool success, uint256 amount) external {
        require(reputations[user].lastUpdated > 0, "User not registered");

        ReputationData storage rep = reputations[user];
        rep.totalTransactions++;
        rep.totalVolume += amount;

        if (success) {
            rep.completedTransactions++;
            if (rep.score < 1000) {
                rep.score = rep.score < 990 ? rep.score + 10 : 1000;
            }
        } else {
            rep.disputes++;
            if (rep.score > 0) {
                rep.score = rep.score > 10 ? rep.score - 10 : 0;
            }
        }

        rep.lastUpdated = block.timestamp;
        emit ReputationUpdated(user, rep.score);
    }

    function updateTransactionStats(
        address user,
        uint256 totalTransactions,
        uint256 completedTransactions,
        uint256 disputes,
        uint256 totalVolume
    ) external onlyOwner {
        require(reputations[user].lastUpdated > 0, "User not registered");

        reputations[user].totalTransactions = totalTransactions;
        reputations[user].completedTransactions = completedTransactions;
        reputations[user].disputes = disputes;
        reputations[user].totalVolume = totalVolume;
        reputations[user].lastUpdated = block.timestamp;
    }

    function getReputation(address user) external view returns (uint256) {
        return reputations[user].score;
    }

    function getReputationData(address user) external view returns (ReputationData memory) {
        return reputations[user];
    }

    function getUserByEmail(string calldata email) external view returns (address) {
        return emailToAddress[email];
    }

    function isUserRegistered(address user) external view returns (bool) {
        return reputations[user].lastUpdated > 0;
    }

    function isVendor(address user) external view returns (bool) {
        return reputations[user].isVendor;
    }
}

contracts/governance/DisputeResolution.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "../interfaces/ISynkioProtocol.sol";

contract DisputeResolution is Ownable, ReentrancyGuard {
    enum DisputeStatus {
        Open,
        InProgress,
        ResolvedBuyer,
        ResolvedSeller,
        Cancelled
    }

    struct Arbitrator {
        address arbitrator;
        uint256 stake;
        bool active;
        uint256 totalCases;
        uint256 successfulCases;
        uint256 lastActive;
    }

    struct DisputeCase {
        uint256 escrowId;
        address initiator;
        address arbitrator;
        string reason;
        string evidence;
        uint256 createdAt;
        uint256 deadline;
        DisputeStatus status;
        bool buyerWins;
        uint256 arbitratorFee;
    }

    uint256 public constant ARBITRATOR_STAKE = 1 ether;
    uint256 public constant ARBITRATOR_FEE = 0.1 ether;
    uint256 public constant DISPUTE_TIMEOUT = 7 days;
    uint256 public constant ARBITRATION_TIMEOUT = 3 days;

    mapping(address => Arbitrator) public arbitrators;
    mapping(uint256 => DisputeCase) public disputes;
    mapping(address => uint256[]) public arbitratorCases;

    address public escrowManager;
    uint256 public totalDisputes;
    uint256 public totalArbitrators;

    event ArbitratorRegistered(address indexed arbitrator, uint256 stake);
    event ArbitratorDeactivated(address indexed arbitrator);
    event DisputeAssigned(uint256 indexed escrowId, address indexed arbitrator);
    event DisputeResolved(uint256 indexed escrowId, address indexed arbitrator, bool buyerWins);
    event ArbitratorStakeSlashed(address indexed arbitrator, uint256 amount);

    modifier onlyArbitrator() {
        require(arbitrators[msg.sender].active, "Not an active arbitrator");
        _;
    }

    modifier onlyEscrowManager() {
        require(msg.sender == escrowManager, "Only escrow manager");
        _;
    }

    constructor() Ownable(msg.sender) {
        _transferOwnership(msg.sender);
    }

    function setEscrowManager(address _escrowManager) external onlyOwner {
        escrowManager = _escrowManager;
    }

    function registerArbitrator() external payable {
        require(msg.value >= ARBITRATOR_STAKE, "Insufficient stake");
        require(!arbitrators[msg.sender].active, "Already registered");

        arbitrators[msg.sender] = Arbitrator({
            arbitrator: msg.sender,
            stake: msg.value,
            active: true,
            totalCases: 0,
            successfulCases: 0,
            lastActive: block.timestamp
        });

        totalArbitrators++;

        emit ArbitratorRegistered(msg.sender, msg.value);
    }

    function deactivateArbitrator() external onlyArbitrator {
        Arbitrator storage arbitrator = arbitrators[msg.sender];
        arbitrator.active = false;

        // Return stake
        (bool success, ) = msg.sender.call{value: arbitrator.stake}("");
        require(success, "Stake return failed");

        arbitrator.stake = 0;

        emit ArbitratorDeactivated(msg.sender);
    }

    function assignDispute(
        uint256 escrowId,
        address initiator,
        string memory reason,
        string memory evidence
    ) external onlyEscrowManager returns (address) {
        require(disputes[escrowId].createdAt == 0, "Dispute already exists");

        // Find available arbitrator
        address arbitrator = _findAvailableArbitrator();
        require(arbitrator != address(0), "No available arbitrators");

        disputes[escrowId] = DisputeCase({
            escrowId: escrowId,
            initiator: initiator,
            arbitrator: arbitrator,
            reason: reason,
            evidence: evidence,
            createdAt: block.timestamp,
            deadline: block.timestamp + ARBITRATION_TIMEOUT,
            status: DisputeStatus.InProgress,
            buyerWins: false,
            arbitratorFee: ARBITRATOR_FEE
        });

        arbitratorCases[arbitrator].push(escrowId);
        arbitrators[arbitrator].totalCases++;
        arbitrators[arbitrator].lastActive = block.timestamp;
        totalDisputes++;

        emit DisputeAssigned(escrowId, arbitrator);

        return arbitrator;
    }

    function resolveDispute(uint256 escrowId, bool buyerWins, string memory reasoning) 
        external 
        onlyArbitrator 
        nonReentrant 
    {
        DisputeCase storage dispute = disputes[escrowId];
        require(dispute.arbitrator == msg.sender, "Not assigned arbitrator");
        require(dispute.status == DisputeStatus.InProgress, "Not under review");
        require(block.timestamp <= dispute.deadline, "Arbitration timeout");

        dispute.status = DisputeStatus.ResolvedBuyer;
        dispute.buyerWins = buyerWins;

        // Update arbitrator stats
        arbitrators[msg.sender].successfulCases++;

        // Pay arbitrator fee
        (bool success, ) = msg.sender.call{value: dispute.arbitratorFee}("");
        require(success, "Arbitrator fee payment failed");

        // Call escrow manager to resolve
        IEscrowManager(escrowManager).resolveDispute(escrowId, buyerWins);

        emit DisputeResolved(escrowId, msg.sender, buyerWins);
    }

    function slashArbitrator(address arbitrator, uint256 amount) external onlyOwner {
        Arbitrator storage arb = arbitrators[arbitrator];
        require(arb.active, "Arbitrator not active");
        require(amount <= arb.stake, "Amount exceeds stake");

        arb.stake -= amount;

        if (arb.stake == 0) {
            arb.active = false;
        }

        emit ArbitratorStakeSlashed(arbitrator, amount);
    }

    function _findAvailableArbitrator() internal view returns (address) {
        // Simple round-robin selection
        // In production, this could be more sophisticated (reputation-based, load balancing, etc.)
        uint256 currentBlock = block.number;
        uint256 arbitratorIndex = currentBlock % totalArbitrators;

        uint256 count = 0;
        for (uint256 i = 0; i < totalArbitrators; i++) {
            address arbitrator = address(uint160(arbitratorIndex + i));
            if (arbitrators[arbitrator].active) {
                count++;
                if (count > arbitratorIndex) {
                    return arbitrator;
                }
            }
        }

        return address(0);
    }

    function getArbitratorStats(address arbitrator) external view returns (
        uint256 totalCases,
        uint256 successfulCases,
        uint256 successRate,
        bool active
    ) {
        Arbitrator memory arb = arbitrators[arbitrator];
        totalCases = arb.totalCases;
        successfulCases = arb.successfulCases;
        successRate = arb.totalCases > 0 ? (arb.successfulCases * 100) / arb.totalCases : 0;
        active = arb.active;
    }

    function getDispute(uint256 escrowId) external view returns (DisputeCase memory) {
        return disputes[escrowId];
    }

    function getArbitratorCases(address arbitrator) external view returns (uint256[] memory) {
        return arbitratorCases[arbitrator];
    }

    function isArbitratorActive(address arbitrator) external view returns (bool) {
        return arbitrators[arbitrator].active;
    }

    receive() external payable {}
}

interface IEscrowManager {
    function resolveDispute(uint256 escrowId, bool buyerWins) external;
}

contracts/interfaces/ISynkioProtocol.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

interface ISynkioProtocol {
    struct Escrow {
        uint256 id;
        address buyer;
        address seller;
        uint256 amount;
        uint256 platformFee;
        uint256 createdAt;
        uint256 expiresAt;
        EscrowStatus status;
        string description;
        bytes32 metadataHash;
        address token;
    }

    enum EscrowStatus {
        Pending,
        Funded,
        Completed,
        Disputed,
        Cancelled,
        Expired
    }

    struct Milestone {
        uint256 amount;
        string description;
        bool completed;
        uint256 completedAt;
    }

    struct Dispute {
        uint256 escrowId;
        address initiator;
        string reason;
        uint256 createdAt;
        DisputeStatus status;
        address arbitrator;
    }

    enum DisputeStatus {
        Pending,
        UnderReview,
        Resolved,
        Rejected
    }

    event EscrowCreated(uint256 indexed escrowId, address indexed buyer, address indexed seller, uint256 amount);
    event EscrowFunded(uint256 indexed escrowId, uint256 amount);
    event EscrowCompleted(uint256 indexed escrowId, uint256 amountReleased);
    event EscrowDisputed(uint256 indexed escrowId, address indexed initiator, string reason);
    event DisputeResolved(uint256 indexed escrowId, address indexed arbitrator, bool buyerWins);
    event ReputationUpdated(address indexed user, uint256 newScore, uint256 totalTransactions);
    event TokenSupported(address indexed token, bool supported);
    event TokenAdded(address indexed token, uint256 indexed chainId, bool active);
}

